<script>
let currentTab = "";

function showTab(tab) {
    if (currentTab === tab) {
        document.body.style.marginTop = "0px";
        hideTabs();
        currentTab = "";
    } else {
        hideTabs();
        document.getElementsByClassName(tab)[0].style.display = "flex";
        document.getElementById(tab + "_button").style.background = "#aaa";
        document.body.style.marginTop = "-600px";
        currentTab = tab;
    }
}

function hideTabs() {
    document.getElementById("cdcontrols_button").style.background = "#000";
    document.getElementById("usbcontrols_button").style.background = "#000";
    document.getElementById("radiocontrols_button").style.background = "#000";
    document.getElementById("powercontrols_button").style.background = "#000";
    document.getElementsByClassName('cdcontrols')[0].style.display = "none";
    document.getElementsByClassName('usbcontrols')[0].style.display = "none";
    document.getElementsByClassName('powercontrols')[0].style.display = "none";
    document.getElementsByClassName('radiocontrols')[0].style.display = "none";
}


</script>


<script>
        // Function to handle click on the progress bar
        document.getElementById('vlcposition').addEventListener('click', function(event) {
            // Get the width of the progress bar
            var progressBar = event.currentTarget;
            var progressBarWidth = progressBar.offsetWidth;

            // Calculate the percentage where the click occurred
            var clickX = event.pageX - progressBar.offsetLeft;
            var percentage = (clickX / progressBarWidth) * 100;

            // Send the seek command to the server
            fetch('api.php?c=seek&val=' + encodeURIComponent(percentage.toFixed(0)) + '%');
        });
    </script>


<script>
let currentPlaat = "";

function spinPlaat() {
	var canvas = document.getElementById("thumbnail");
	
    document.getElementById('thumbnailAnimation').classList.add("paused");

	//canvas.className = "";
	var spins = 360 * 7;
	// Define keyframes for the initial animation
	const initialKeyframes = [
	  { transform: 'rotate(0deg)' },
	  { transform: 'rotate('+spins+'deg)' }
	];

	// Define timing options for the initial animation
	const initialOptions = {
	  duration: 2000, // duration in milliseconds
	  easing: 'cubic-bezier(.07,.88,.56,1)', // increased easing
	  fill: 'both'
	};

	canvas.animate(initialKeyframes, initialOptions);
	
	document.getElementById('thumbnailAnimation').classList.remove("paused");
}

async function setBgImage(imgSrc) {
    // Function to load an image
    function loadImage(src) {
        return new Promise((resolve, reject) => {
            const img = new Image();
            img.crossOrigin = "Anonymous"; // Needed for cross-origin images
            img.onload = () => resolve(img);
            img.onerror = reject;
            img.src = src;
        });
    }

    // Function to apply blur effect using canvas
    async function applyBlurEffect(image) {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');

        canvas.width = image.width;
        canvas.height = image.height;

        // Draw the image on the canvas
        ctx.drawImage(image, 0, 0);

        // Apply blur effect
        ctx.filter = 'blur(10px)';
        ctx.drawImage(canvas, 0, 0);

		ctx.filter = 'blur(10px)';
        ctx.drawImage(canvas, 0, 0);

		ctx.filter = 'blur(10px)';
        ctx.drawImage(canvas, 0, 0);

        // Return the processed image as a data URL
        return canvas.toDataURL();
    }

    try {
        const image = await loadImage(imgSrc);
        const blurredImage = await applyBlurEffect(image);

        // Get the img elements
        const currentImage = document.getElementById('background');
        const nextImage = document.getElementById('background-next');

        // Set the new image source on the next image
        nextImage.src = blurredImage;

        // Make the next image visible
        nextImage.classList.remove('hidden');
        nextImage.classList.add('visible');

        // Make the current image hidden
        currentImage.classList.remove('visible');
        currentImage.classList.add('hidden');

        // After the transition, swap the IDs
        setTimeout(() => {
            // Swap the IDs back
            currentImage.id = 'background-next';
            nextImage.id = 'background';
        }, 500); // Match this duration with the transition duration
    } catch (error) {
        console.error('Error loading or processing image:', error);
    }
}





function drawPlaat(img) {
	
	if (currentPlaat == img) {
		return;
	}
	currentPlaat = img;
	
	spinPlaat();
	
   

	var canvas = document.getElementById("thumbnail");
	var ctx = canvas.getContext("2d");
	var image = new Image();
	image.onload = function() {
		// Determine the size for a square canvas
		var size = Math.min(300, 300);
		canvas.width = size;
		canvas.height = size;
		var radius = size / 2;
		var x = size / 2;
		var y = size / 2;
		// Draw the black vinyl record
		ctx.beginPath();
		ctx.arc(x, y, radius, 0, Math.PI * 2, true);
		ctx.fillStyle = "black";
		ctx.fill();
		ctx.closePath();
		// Create a circular clipping path for the image
		ctx.beginPath();
		ctx.arc(x, y, radius * 0.99, 0, Math.PI * 2, true);
		ctx.closePath();
		ctx.clip();
		// Draw the image within the clipping path
		ctx.drawImage(image, (canvas.width - size) / 2, (canvas.height - size) / 2, size, size);
		
		
		if (cdPlaying) {
			CanvasRenderingContext2D.prototype.fillTextCircle = function(text,x,y,radius,startRotation){
			   var numDegreesPerLetter = 2*Math.PI / text.length;
			   this.save();
			   this.translate(x,y);
			   this.rotate(startRotation);

			   for(var i=0;i<text.length;i++){
				  this.save();
				  this.translate(radius, 0);
			//      if (i == 0) {
			//          this.fillStyle = 'red';
					  this.translate(10, -10);
			//          this.fillRect(0,0,4,4);
					  this.rotate(1.4)
					  this.translate(-10, 10);          
			//          this.fillStyle = 'black';
			//      }

			//      this.fillRect(0,0,4,4);
				  this.fillText(text[i],0,0);
				  this.restore();
				  this.rotate(numDegreesPerLetter);
			   }
			   this.restore();
			}
				
			ctx.fillStyle = "white";
			//ctx.font = "bold 40px Serif";
			//ctx.fillTextCircle(document.getElementById('title').innerHTML + " ",x,y,130,Math.PI / 2);
			ctx.font = "30px Serif";
			ctx.fillTextCircle(document.getElementById('artist').innerHTML + " ",x,y,80,Math.PI / 2);
		}
		
		if (img.includes('radioart/')) {
			ctx.font = "bold 20px Arial";
			ctx.fillStyle = "white";
			ctx.textAlign = "center";
			ctx.fillText(document.getElementById('title').innerHTML,x,180);
		}
  
  
		// Draw grooves
		ctx.strokeStyle = "rgba(255, 255, 255, 0.2)"; // Light white for grooves
		ctx.lineWidth = 1;
		for (var r = radius * 0.1; r < radius; r += radius * 0.05) {
			ctx.beginPath();
			ctx.arc(x, y, r, 0, Math.PI * 2, true);
			ctx.stroke();
			ctx.closePath();
		}
		// Draw the center label of the vinyl record
		ctx.beginPath();
		ctx.arc(x, y, radius * 0.3, 0, Math.PI * 2, true);
		ctx.fillStyle = "rgba(255, 255, 255, 0.3)";
		ctx.fill();
		ctx.closePath();
		// Draw the center label of the vinyl record
		ctx.beginPath();
		ctx.arc(x, y, radius * 0.02, 0, Math.PI * 2, true);
		ctx.fillStyle = "black";
		ctx.fill();
		ctx.closePath();
		// Add grainy texture
		var imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
		var pixels = imageData.data;
		for (var i = 0; i < pixels.length; i += 4) {
			var noise = (Math.random() - 0.5) * 30; // Adjust the multiplier for more/less noise
			pixels[i] += noise; // Red
			pixels[i + 1] += noise; // Green
			pixels[i + 2] += noise; // Blue
			// Alpha (pixels[i + 3]) remains unchanged
		}
		ctx.putImageData(imageData, 0, 0);
	};
	
	//setBgImage(img);
	
	
	image.src = img;

	setBgImage(img);
	
}
//drawPlaat('blank.png');
</script>

<script>
let currentCdTrack = 0;
let maxCdTrack = 0;
let vlcState = "stopped";
let vlcRepeat = false;
let vlcRandom = false;
let cdPlaying = false;
let mountPoints = 0;

function fetchStatus() {
    fetch('api.php')
        .then(response => response.json()) // Parse the JSON response
        .then(data => {
            //console.log(data);

            vlcState = data.vlc_status.state;
            vlcRepeat = data.vlc_status.repeat;
            vlcRandom = data.vlc_status.random;
            cdPlaying = data.cd_status.playing;

            if (vlcRepeat) {
                document.getElementById('repeat').style.background = "#aaa";
            } else {
                document.getElementById('repeat').style.background = "#000";
            }

            if (vlcRandom) {
                document.getElementById('random').style.background = "#aaa";
            } else {
                document.getElementById('random').style.background = "#000";
            }

            if (vlcState == "paused") {
                document.getElementById('pause').style.background = "#aaa";
            } else {
                document.getElementById('pause').style.background = "#000";
            }

            if (vlcState == "stopped") {
                document.getElementById('stop').style.background = "#aaa";
            } else {
                document.getElementById('stop').style.background = "#000";
            }
			
			if (vlcState == "playing") {
				document.getElementById('thumbnailAnimation').classList.remove("paused");
			} else {
				document.getElementById('thumbnailAnimation').classList.add("paused");
			}

			//if (data.vlc_status.stream.codec != "undefined") {
				let codec = data.vlc_status.stream.codec;
				let bitrate = data.vlc_status.stream.bitrate;
				let samplerate = data.vlc_status.stream.samplerate;

				let values = [];

				if (codec !== null && codec !== undefined) {
					values.push(codec);
				}

				if (bitrate !== null && bitrate !== undefined) {
					values.push(bitrate);
				}

				if (samplerate !== null && samplerate !== undefined) {
					values.push(samplerate);
				}

				document.getElementById('stream').innerHTML = values.join(" | ");
			//}

            if (data.cd_status.playing == true) {
                document.getElementById('title').innerHTML = data.cd_status.track_info.title;
                currentCdTrack = data.cd_status.track;
                maxCdTrack = data.cd_status.track_count;
                document.getElementById('artist').innerHTML = data.cd_status.track_info.tracks[`track_${currentCdTrack - 1}`];
				document.getElementById('album').innerHTML = "...";
                drawPlaat('blank2.png?' + currentCdTrack);
				document.getElementById('vlcposition').value = data.vlc_status.position;
            } else {
				if (data.vlc_status.meta.artist.includes("RADIOSTREAM;")) {
					let img = data.vlc_status.meta.artist.split(';')[1];
					document.getElementById('title').innerHTML = data.vlc_status.meta.title;
					document.getElementById('artist').innerHTML = "streaming...";
					document.getElementById('album').innerHTML = "...";
					if (img == "undefined" || img == "" || img == null) {
						drawPlaat('radioart/radio.jpg?' + data.vlc_status.meta.title);
					} else {
						drawPlaat(img + "?" + data.vlc_status.meta.title);
					}
					document.getElementById('vlcposition').value = 1;
					
					
				} else {
					let title = data.vlc_status.meta.title;
					if (title == "...") {
						title = data.vlc_status.meta.filename;
					}
					document.getElementById('title').innerHTML = title;
					document.getElementById('artist').innerHTML = data.vlc_status.meta.artist;
					document.getElementById('album').innerHTML = data.vlc_status.meta.album;
					if (data.vlc_status.meta.artwork_url == null || data.vlc_status.meta.artwork_url == "" || data.vlc_status.meta.artwork_url == "art.php?f=") {
						drawPlaat('blank2.png?t=' + btoa(unescape(encodeURIComponent(data.vlc_status.meta.filename))));
					} else {
						drawPlaat(data.vlc_status.meta.artwork_url + "&t=" + btoa(unescape(encodeURIComponent(data.vlc_status.meta.filename))));
					}
					document.getElementById('vlcposition').value = data.vlc_status.position;
				}
                
            }

            if (data.cd_status.mounted == true) {
                document.getElementById('cdtrackslist').innerHTML = "";
                document.getElementById('cdtrackslist').innerHTML += "<h2>" + data.cd_status.track_info.title + "</h2><br>";
                Object.entries(data.cd_status.track_info.tracks).forEach(([key, track]) => {
                    //console.log(key, track);
                    let spanElement = `<span id="${key}">${track}</span><br>`;
                    document.getElementById('cdtrackslist').innerHTML += spanElement;
                });
                if (data.cd_status.playing == true) {
                    document.getElementById('track_' + (data.cd_status.track - 1)).style.color = "white";
                }
            } else {
				document.getElementById('cdtrackslist').innerHTML = "";
			}


			if (mountPoints != data.usb_status.devices.length) {
				document.getElementById('fileBrowser').src = document.getElementById('fileBrowser').src;
				 //Refresh
			}
			mountPoints = data.usb_status.devices.length;

		
          
            
        })
}
fetchStatus();
setInterval(fetchStatus, 1000);


function createRipple(event) {
  const button = event.currentTarget;

  const circle = document.createElement("span");
  const diameter = Math.max(button.clientWidth, button.clientHeight);
  const radius = diameter / 2;

  circle.style.width = circle.style.height = `${diameter}px`;
  circle.style.left = `${event.clientX - button.offsetLeft - radius}px`;
  circle.style.top = `${event.clientY - button.offsetTop - radius}px`;
  circle.classList.add("ripple");

  const ripple = button.getElementsByClassName("ripple")[0];

  if (ripple) {
    ripple.remove();
  }

  button.appendChild(circle);
}

const buttons = document.getElementsByTagName("button");
for (const button of buttons) {
  button.addEventListener("click", createRipple);
}


</script>


